const initialState = { loggedUser : null };

const reducer = (state = initialState, action) => {
  switch(action.type)
  {
    case 'LOG IN' : return { loggedUser : action.payload };

    case 'REGISTER' : return { loggedUser : action.payload };
   
    case 'LOG OUT' : return { loggedUser : action.payload };

    default : return state;

  }
}

export default reducer;
