import React from "react";
import Homepage from "../home/Homepage";
import "./Register.css";
import { useState, useEffect } from "react";
import { useNavigate, Route } from "react-router-dom";
import axios from "axios";

function Register({setloggedUser}) {
  const initialValues = { fname: "", lname: "", email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [messageNote, setMessageNote] = useState("");
  const [isSubmit, setIsSubmit] = useState(false);
  const navigate = useNavigate()

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      //console.log(formValues);
      setIsSubmit(false);
      
      axios.post("http://localhost:9002/Register", {
        formValues
      })
      .then(function (res) {
        if(res.data.message === "successfull"){
          //alert("Registered successfully!")
          setMessageNote("Registered Successfully!")
          setloggedUser(formValues.email)
          localStorage.setItem('token', formValues.email);
          <Route path="/Homepage"><Homepage setloggedUser={setloggedUser}/></Route>
        }
        else{
          alert(res.data.message)
        }
      })
      .catch(function (error) {
        console.log(error);
      });

    }
  }, [formErrors]);


  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.fname) {
      errors.fname = "First name is required!";
    }
    if (!values.lname) {
      errors.lname = "Last name is required!";
    }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required!";
    } else if (values.password.length < 4) {
      errors.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };

  return (
    <>
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 col-xs-10 offset-xs-1 form-container">
          <div className="row">
            <div className="col-12">
              <h2 className="form-heading text-center text-muted">
                Register yourself
              </h2>
            </div>
          </div>
          <hr />
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col form-group">
                <input
                  type="text"
                  name="fname"
                  className="form-control"
                  placeholder="First name"
                  value={formValues.fname}
                  onChange={handleChange}
                />
                <p className="text-muted err-msg">{formErrors.fname}</p>
              </div>
              <div className="col form-group">
                <input
                  type="text"
                  name="lname"
                  className="form-control"
                  placeholder="Last name"
                  value={formValues.lname}
                  onChange={handleChange}
                />
                <p className="err-msg text-muted">{formErrors.lname}</p>
              </div>
            </div>
            <div className="row">
              <div className="col-12 form-group">
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  id="email"
                  placeholder="Enter email"
                  value={formValues.email}
                  onChange={handleChange}
                />
                <p className="err-msg text-muted">{formErrors.email}</p>
              </div>
            </div>
            <div className="row">
              <div className="col-12 form-group">
                <input
                  type="password"
                  name="password"
                  className="form-control"
                  id="pwd"
                  placeholder="Enter password"
                  value={formValues.password}
                  onChange={handleChange}
                />
                <p className="err-msg text-muted">{formErrors.password}</p>
              </div>
            </div>
            <div className="row">
              <div className="col-12">
                <button type="submit" className="btn btn-primary btn-block btn-register">
                Register
                </button>
              </div>
            </div>
            <div className="row">
              <div className="col-12">
                <p className="comment text-center text-muted">
                  Already have account?
                </p>
              </div>
            </div>
            <div className="row">
              <div className="col-12">
                <button type="button" onClick={()=>navigate("/")} className="btn btn-primary btn-block btn-signin">
                  Sign In
                </button>
              </div>
            </div>
            <div className="row">
              <div className="col-12">
              <p className="comment text-center text-muted">
                  {messageNote}
                </p>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
    </>
  );
}

export default Register;
